!function(){const e=document.querySelector(".header");window.onscroll=()=>{window.pageYOffset>450?(e.classList.add("header__active"),document.querySelector(".header__logo-img").setAttribute("src","img/YourTourBlack.svg")):(e.classList.remove("header__active"),document.querySelector(".header__logo-img").setAttribute("src","img/YourTourWhite.svg"))}}();